import React, { Component } from "react";
import Input from "../../general/Input";
import {withRouter} from "react-router-dom";
import {connect } from "react-redux";
import {addproduct} from "../../../actions/productAction" 
class AddProduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      price: "",
      quantity: "",
      category: "",
      brand: "",
    };
  }

  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = (e) => {
    e.preventDefault();
    const { name, description, price, brand, quantity, category } = this.state;
    const newProduct = {
      name,
      description,
      price,
      brand,
      quantity,
      category,
    };
    console.log("Product submitted: ", newProduct);
    this.props.addproduct(newProduct, this.props.h)
  };

  render() {
    const { name, description, price, brand, quantity, category } = this.state;

    return (
      <div style={{ textAlign: "center" }}>
        <h1>Add Product</h1>
        <form onSubmit={this.onSubmit}>
          <Input
            type="text"
            placeholder="Name of product"
            name="name"
            value={name}
            onChange={this.onChange}
          />
          <Input
            type="text"
            placeholder="Give a brief description of the product"
            name="description"
            value={description}
            onChange={this.onChange}
          />
          <Input
            type="number"
            placeholder="Enter the price of the product"
            name="price"
            value={price}
            onChange={this.onChange}
          />
          <Input
            type="text"
            placeholder="Enter the brand of the product"
            name="brand"
            value={brand}
            onChange={this.onChange}
          />
          <Input
            type="number"
            placeholder="Number of the product"
            name="quantity"
            value={quantity}
            onChange={this.onChange}
          />
          <div>
            <select name="category" value={category} onChange={this.onChange}>
              <option value="0">Select a category for the product</option>
              <option value="Clothing">Clothing</option>
              <option value="Electronics">Electronics</option>
              <option value="Furniture">Furniture</option>
              <option value="Toys">Toys</option>
              <option value="Books">Books</option>
            </select>
          </div>
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps=(state)=>({
  product: state.products
})

export default connect(mapStateToProps, {addproduct})(withRouter(AddProduct));
