import axios from "axios";
import { GET_PRODUCTS, PORDUCT_ERRROR } from "./types";
import { getServer } from "../utill";
export const getproduct = () => async (dispatch) => {
  try {
    const res = await axios.get(`${getServer()}/api/products`);
    dispatch({
      type: GET_PRODUCTS,
      payload: res.data,
    });
  } catch (err) {
    dispatch({
      type: PORDUCT_ERRROR,
      payload: { status: err.response.status },
    });
  }
};

export const addproduct = (productData, history) => async (dispatch) => {
  const config = {
    headers: {
      "Content-Type": "application/json",
    },
  };
  try {
    await axios.post(`${getServer()}/api/products`, productData, config);
    history.push("/dashboard/products");
  } catch (err) {
    dispatch({
      type: PORDUCT_ERRROR,
      payload: { status: err.response.status },
    });
  }
};

export const getInstructorProduct = (id) => async (dispatch) => {
  try {
    await axios
    .get(`${getServer()}/api/product/instructor/${id}`)
    .then(res=>
      dispatch({
        type: GET_PRODUCTS
      }))
  } catch (error) {
    dispatch({
      type: PORDUCT_ERRROR,
      payload: { status: err.response.status },
    });
  }
};
