import React, { Component } from "react";
import { connect } from "react-redux";
import { getInstructorProduct } from "../../../actions/productAction";
import Product from "../../general/Product";
import { decodeUser } from "../../../utill";
class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merachantProducts: [],
    };
  }
  componentDidMount() {
    this.props.getInstructorProduct(decodeUser().user.Id);
    // console.log("decodeUser==>>",decodeUser());
    // console.log("user.Id==>>",user.Id);
  }
  componentWillReceiveProps(nextProps) {
    console.log(nextProps.products.products);
    // if (
    //   nextProps &&
    //   nextProps.products &&
    //   nextProps.products.products.length > 0
    // ) {
    //   const userId = decodeUser().user.id;
    //   let merachantProducts = [];
    //   merachantProducts = nextProps.products.products.filter(
    //     (product) => product.userId === userId
    //   );
    //   this.setState({ merachantProducts });
    // }
    debugger;
  }
  productDetails = (product) => () => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };
  render() {
    const { merachantProducts } = this.state;
    return (
      <div className="row">
        {merachantProducts.map((product, index) => (
          <Product
          key={product._id}
          product={product}
          description={this.productDetails(product)}
          buttonName="Add img"
          />
        ))}
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  products: state.products,
});
export default connect(mapStateToProps, { getInstructorProduct })(Products);
