import React, { useState } from "react";
import Input from "../../general/Input";
import { connect } from "react-redux";
import { addproduct } from "../../../actions/productAction";
import { useHistory } from "react-router-dom";
import { message } from "antd";

const AddProduct = ({ addproduct }) => {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    quantity: "",
    category: "",
    brand: "",
  });

  const history = useHistory();

  const { name, description, price, brand, quantity, category } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    if (name.length === 0) {
      return message.error("Name field is required");
    }
    if (description.length === 0) {
      return message.error("Description field is required");
    }
    if (price.length === 0) {
      return message.error("Price field is required");
    }
    if (brand.length === 0) {
      return message.error("Brand field is required");
    }
    if (quantity.length === 0) {
      return message.error("Quantity field is required");
    }

    const newProduct = {
      name,
      description,
      price,
      brand,
      quantity,
      category,
    };

    await addproduct(newProduct);
    history.push("/dashboard/product");
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Add Product</h1>
      <form onSubmit={onSubmit}>
        <Input
          type="text"
          placeholder="Name of product"
          name="name"
          value={name}
          onChange={onChange}
        />
        <Input
          type="text"
          placeholder="Give a brief description of the product"
          name="description"
          value={description}
          onChange={onChange}
        />
        <Input
          type="number"
          placeholder="Enter the price of the product"
          name="price"
          value={price}
          onChange={onChange}
        />
        <Input
          type="text"
          placeholder="Enter the brand of the product"
          name="brand"
          value={brand}
          onChange={onChange}
        />
        <Input
          type="number"
          placeholder="Number of the product"
          name="quantity"
          value={quantity}
          onChange={onChange}
        />
        <div>
          <select name="category" value={category} onChange={onChange}>
            <option value="0">Select a category for the product</option>
            <option value="Clothing">Clothing</option>
            <option value="Electronics">Electronics</option>
            <option value="Furniture">Furniture</option>
            <option value="Toys">Toys</option>
            <option value="Books">Books</option>
          </select>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default connect(null, { addproduct })(AddProduct);
