import React, { Component } from "react";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";
import Product from "../../general/Product";
class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merachantProducts: [],
    };
  }
  componentDidMount() {
    this.props.getproduct();
  }
  componentWillReceiveProps(nextProps) {
    if (
      nextProps &&
      nextProps.products &&
      nextProps.products.products.length > 0
    ) {
      const userId = nextProps.auth.user._id;
      let merachantProducts = [];
      merachantProducts = nextProps.products.products.filter(
        (product) => product.userId === userId
      );
      this.setState({ merachantProducts });
    }
  }
  productDetails = (product) => () => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };
  render() {
    const { merachantProducts } = this.state;
    return (
      <div className="row">
        {merachantProducts.map((product, index) => (
          <Product
            key={product._id}
            product={product}
            description={this.productDetails(product)}
            buttonName="addimg"
          />
        ))}
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  products: state.products,
  auth: state.auth,
});
export default connect(mapStateToProps, { getproduct })(Products);
