import React, { Component } from "react";
import Input from "../../general/Input";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { addproduct } from "../../../actions/productAction";

class Addproduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      price: "",
      brand: "",
      quantity: "",
      category: "",
    };
  }
  onChange = (e) => {
    console.log(e.target);
    this.setState({ [e.target.name]: e.target.value });
  };

  onSubmit = () => {
    const { name, description, price, brand, quantity, category } = this.state;
    const newProduct = {
      name,
      description,
      price,
      brand,
      quantity,
      category,
    };
    console.log(newProduct);
    this.props.Addproduct(newProduct, this.props.hist)
  };
  render() {
    const { name, description, price, brand, quantity, category } = this.state;
    return (
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <br />
        <h5>Add Product</h5>
        <br />
        <Input
          name="name"
          type="text"
          placeholder="name of product"
          value={name}
          onChange={this.onChange}
        />
        <br />
        <Input
          name="description"
          type="text"
          placeholder="description"
          value={description}
          onChange={this.onChange}
        />
        <br />
        <Input
          name="price"
          type="text"
          placeholder="enter the price"
          value={price}
          onChange={this.onChange}
        />
        <br />
        <Input
          name="brand"
          type="text"
          placeholder="enter the price"
          value={brand}
          onChange={this.onChange}
        />
        <br />
        <Input
          name="quantity"
          type="text"
          placeholder="enter the quantity"
          value={quantity}
          onChange={this.onChange}
        />
        <br />
        <div>
          <select
            className="from-control"
            name="category"
            value={category}
            onChange={this.onChange}
            style={{ width: "200px", height: "30px" }}
          >
            <option value="0">Select a category</option>
            <option value="Clothing">Clothing</option>
            <option value="Electonics">Electonics</option>
            <option value="Office Supply">Office Supply</option>
            <option value="Automotive supply">Automotive supply</option>
            <option value="Cosmetics">Cosmetics</option>
          </select>
        </div>
        <br />
        <button
          className="btn btn-primary"
          onClick={this.onSubmit}
          style={{ textAlign: "center", padding: "0px" }}
        >
          Submit
        </button>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  products: state.products,
});
export default connect(mapStateToProps, { addproduct })(withRouter(Addproduct));
