import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getInstructorProduct } from "../../../actions/productAction";
import Product from "../../general/Product";

const Products = () => {
  const [merachantProducts, setMerachantProducts] = useState([]);
  const [userId, setUserId] = useState(null);
  
  const dispatch = useDispatch();
  const products = useSelector(state => state.products.products);

  useEffect(() => {
    const storedUserId = localStorage.getItem('userId');
    if (storedUserId) {
      setUserId(storedUserId);
      dispatch(getInstructorProduct(storedUserId));
    } else {
      console.error("User ID is undefined in localStorage");
    }
  }, [dispatch]);

  useEffect(() => {
    if (products && products.length > 0) {
      setMerachantProducts(products);
    }
  }, [products]);

  const productDetails = (product) => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };

  return (
    <div className="row">
      {merachantProducts.map((product) => (
        <Product
          key={product._id}
          product={product}
          description={productDetails(product)}
          buttonName="Add img"
        />
      ))}
      {userId && <p>User ID: {userId}</p>}
    </div>
  );
};

export default Products;
