import React, { Component } from "react";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";
import Product from "../../general/Product";

class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merchantProduct: [],
    };
  }

  componentDidMount() {
    this.props.getproduct();
  }

  componentDidUpdate(prevProps) {
    if (
      this.props.product &&
      this.props.product.product &&
      prevProps.product !== this.props.product
    ) {
      const userId = this.props.auth.user._Id;
      let merchantProduct = this.props.product.product.filter(
        (product) => product.userId === userId
      );
      this.setState({ merchantProduct });
    }
  }

  productDetails = (product) => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };

  render() {
    const { merchantProduct } = this.state;
    return (
      <div>
        {merchantProduct.map((product, index) => (
          <Product
            key={index}
            product={product}
            description={() => this.productDetails(product)}
            buttonName="add images"
          />
        ))}
        <p></p>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
  auth: state.auth,
});

export default connect(mapStateToProps, { getproduct })(Products);
