import React, { Component } from "react";
import { connect } from "react-redux";
import { getInstructorProduct } from "../../../actions/productAction";
import Product from "../../general/Product";
import { decodeUser } from "../../../utill";

class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merachantProducts: [],
    };
  }

  componentDidMount() {
    const user = decodeUser();
    if (user && user.user && user.user.Id) {
      this.props.getInstructorProduct(user.user.Id);
    } else {
      console.error("User ID is undefined");
    }
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps && nextProps.products && nextProps.products.products.length > 0) {
      const merachantProducts = nextProps.products.products;
      this.setState({ merachantProducts });
    }
  }

  productDetails = (product) => () => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };

  render() {
    const { merachantProducts } = this.state;
    return (
      <div className="row">
        {merachantProducts.map((product) => (
          <Product
            key={product._id}
            product={product}
            description={this.productDetails(product)}
            buttonName="Add img"
          />
        ))}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
});

export default connect(mapStateToProps, { getInstructorProduct })(Products);
