import React, { Component } from "react";
import { connect } from "react-redux";
import { getInstructorProduct } from "../../../actions/productAction";
import Product from "../../general/Product";
import { decodeUser } from "../../../utill";
class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merachantProducts: [],
    };
  }
  componentDidMount() {
    const user = decodeUser();
    console.log("user==>>",user)
    if(user.user.id){
      console.log("user.id<==",user.user.id)
      this.props.getInstructorProduct(user.user.id)
    }
    else{
      console.log("code issuse")
    }
  }
  // if (user && user.user && user.user.id) {
  //   this.props.getInstructorProduct("user.user.Id==>>",user.user.Id);
  // } else {
  //   console.error("user id is undefined");
  // }
  // this.props.getInstructorProduct(user);
  componentWillReceiveProps(nextProps) {
    // console.log(nextProps.products.products);
    if (
      nextProps &&
      nextProps.products &&
      nextProps.products.products.length > 0
    ) {
      const merachantProducts = nextProps.products.products;
      this.setState({ merachantProducts });
    }
  }
  productDetails = (product) => () => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };
  render() {
    const { merachantProducts } = this.state;
    return (
      <div className="row">
        {merachantProducts.map((product, index) => (
          <Product
            key={product._id}
            product={product}
            description={this.productDetails(product)}
            buttonName="Add img"
          />
        ))}
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  products: state.products,
});
export default connect(mapStateToProps, { getInstructorProduct })(Products);
