import React, { Component } from "react";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";

class Products extends Component {
  constructor(props){
    super()
  }
  componentDidMount() {
    this.props.getproduct();
  }
  componentDidUpdate(nextProps) {
    if (nextProps && nextProps.product && nextProps.product.product > 0) {
      const userId = nextProps.auth.user._Id;
      let merchantProduct = [];
      merchantProduct = nextProps.product.product.filter(
        (product) => product.userId === userId
      );
      this.setState({merchantProduct})
    }
    console.log(nextProps);
  }
  render() {
    return <div>Products</div>;
  }
}

const mapStateToProps = (state) => ({
  product: state.product,
  auth: state.auth,
});
export default connect(mapStateToProps, { getproduct })(Products);
