import React, { Component } from "react";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";

class Products extends Component {
  componentDidMount() {
    this.props.getproduct();
  }
  componentWillReceiveProps(nextProps) {
    if (
      nextProps.products &&
      nextProps.products &&
      nextProps.products.products.length > 0
    ) {
      const userId = nextProps.auth.user._id;
      let merchantProduct = [];
      merchantProduct = nextProps.products.products.filter(
        (product) => product.userId === userId
      );
      console.log(merchantProduct);
    }
  }
  render() {
    const { merchantProduct } =this.state;
    return (
      <div>
        <h3>Products</h3>
        <ul>
          {merchantProduct.map((product) => (
            <li key={product._id}>{product.name}</li>
          ))}
        </ul>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  products: state.Products,
  auth: state.auth,
});

export default connect(mapStateToProps, { getproduct })(Products);
