import React, { Component } from "react";
import Input from "../../general/Input";
export default class Addproduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      price: 0,
      quantitiy: 0,
      category:""
    };
  }
  onChange = () => {};
  render() {
    const { name, description, price, brand, quantitiy,category } = this.state;
    return (
      <div style={{ textAlign: "center" }}>
        <h1>Addproduct</h1>
        <Input
          type="text"
          placeholder="name of product"
          name="name"
          value={name}
          onChange={this.onChange}
        />
        <Input
          type="text"
          placeholder="give a brief description of the product"
          name="name"
          value={description}
          onChange={this.onChange}
        />
        <Input
          type="text"
          placeholder="Enter the price of the product"
          name="price"
          value={price}
          onChange={this.onChange}
        />
        <Input
          type="text"
          placeholder="Enter the brand of the product"
          name="name"
          value={brand}
          onChange={this.onChange}
        />
        <Input
          type="text"
          placeholder="number of the product"
          name="quantitiy"
          value={quantitiy}
          onChange={this.onChange}
        />
        <div>
          <select 
          name="category"
          value={category}
          onChange={this.onChange}
          >
            
          </select>
        </div>
      </div>
    );
  }
}
