import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getInstructorProduct } from "../../../actions/productAction";
import Product from "../../general/Product";
import { decodeUser } from "../../../utill";

const Products = () => {
  const [merchantProducts, setMerchantProducts] = useState([]);
  const dispatch = useDispatch();
  const products = useSelector((state) => state.products);

  useEffect(() => {
    const user = decodeUser();
    if (user && user.user && user.user.id) {
      dispatch(getInstructorProduct(user.user.id));
    } else {
      console.log("code issue");
    }
  }, [dispatch]);

  useEffect(() => {
    if (products && products.products && products.products.length > 0) {
      setMerchantProducts(products.products);
    }
  }, [products]);

  const productDetails = (product) => (
    <ul>
      <li>Rupees: {product.price}</li>
      <li>Quantity: {product.quantity}</li>
    </ul>
  );

  return (
    <div
      style={{
        display: "flex",
        flexWrap: "wrap",
        flexDirection: "row",
        margin: "2%",
        gap: "2%",
        justifyContent: "flex-start",
        rowGap: "20px",
      }}
    >
      {merchantProducts.length > 0 ? (
        merchantProducts.map((product) => (
          <Product
            className="product"
            key={product._id}
            product={product}
            description={productDetails(product)}
            buttonName="Add img"
          />
        ))
      ) : (
        <p>No products available.</p>
      )}
    </div>
  );
};

export default Products;
