import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";
import Product from "../../general/Product";

class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merchantProducts: [],
    };
  }

  componentDidMount() {
    this.props.getproduct();
  }

  componentDidUpdate(prevProps) {
    const { products, auth } = this.props;
    if (products !== prevProps.products && products.products.length > 0) {
      const userId = auth.user._id;
      const merchantProducts = products.products.filter(
        (product) => product.userId === userId
      );
      this.setState({ merchantProducts });
    }
  }

  productDetails = (product) => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };

  render() {
    const { merchantProducts } = this.state;
    return (
      <div className="row">
        {merchantProducts.map((product, index) => (
          <Product
            key={index}
            product={product}
            description={this.productDetails(product)}
            buttonName="Add images"
          />
        ))}
      </div>
    );
  }
}

Products.propTypes = {
  getproduct: PropTypes.func.isRequired,
  products: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  products: state.products,
  auth: state.auth,
});

export default connect(mapStateToProps, { getproduct })(Products);
