import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { getproduct } from "../../../actions/productAction";
import Product from "../../general/Product";

class ProductsSeller extends Component {
  state = {
    merchantProduct: [],
  };

  componentDidMount() {
    this.props.getproduct();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.products !== this.props.products) {
      const { products, auth } = this.props;
      if (products && products.products && auth && auth.user) {
        const userId = auth.user._id; // Assuming userId is stored in auth.user._id
        console.log("User ID:", userId);
        console.log("Products:", products.products);
        const merchantProduct = products.products.filter(
          (prod) => prod.userId === userId
        );
        this.setState({ merchantProduct });
      }
    }
  }

  productDetails = (product) => (
    <ul>
      <li>Rupees: {product.price}</li>
      <li>Quantity: {product.quantity}</li>
    </ul>
  );

  render() {
    const { merchantProduct } = this.state;
    return (
      <div>
        {merchantProduct.length > 0 ? (
          merchantProduct.map((product, index) => (
            <Product
              key={index}
              product={product}
              description={() => this.productDetails(product)}
              buttonName="add images"
            />
          ))
        ) : (
          <p>No products found for this user.</p>
        )}
      </div>
    );
  }
}

ProductsSeller.propTypes = {
  getproduct: PropTypes.func.isRequired,
  products: PropTypes.object.isRequired,
  auth: PropTypes.object.isRequired,
};

ProductsSeller.defaultProps = {
  products: { products: [] },
  auth: { user: {} },
};

const mapStateToProps = (state) => ({
  products: state.product,
  auth: state.auth,
});

export default connect(mapStateToProps, { getproduct })(ProductsSeller);
