import React, { Component } from "react";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";
import Product from "../../general/Product";

class Products extends Component {
  constructor(props){
    super(props)
    this.state={
      merchantProduct:[],
    }
  }
  componentDidMount() {
    this.props.getproduct();
  }
  componentDidUpdate(nextProps) {
    if (nextProps && nextProps.product && nextProps.product.product > 0) {
      const userId = nextProps.auth.user._Id;
      let merchantProduct = [];
      merchantProduct = nextProps.product.product.filter(
        (product) => product.userId === userId
      );
      this.setState({merchantProduct})
    }
    console.log(nextProps);
  }
  productDetails = (product) => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };
  render() {
    const {merchantProduct}=this.state
    return <div className="row">
      {merchantProduct.map((product ,index)=> {
        <Product product={product} description={thisproductDetails} buttonName="add images"/>
      } )}
    </div>;
  }
}

const mapStateToProps = (state) => ({
  product: state.product,
  auth: state.auth,
});
export default connect(mapStateToProps, { getproduct })(Products);
