import React, { Component } from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import { Avatar } from "antd";
import { logout } from "../../actions/authAction";

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      child: props.nestRoute,
      search: "",
    };
  }

  componentDidMount() {
    this.activeNav();
  }

  componentDidUpdate(prevProps) {
    if (this.props.location.pathname !== prevProps.location.pathname) {
      this.activeNav();
    }
  }

  activeNav = () => {
    const pathname = window.location.pathname;
    const possibleRoutes = [
      { route: "/dashboard", targetId: "home" },
      { route: "/dashboard/AddProduct", targetId: "AddProduct" },
      { route: "/dashboard/product", targetId: "product" },
    ];
    possibleRoutes.forEach(({ route, targetId }) => {
      window.jQuery(`#${targetId}`).removeClass("active");
      if (route === pathname) {
        window.jQuery(`#${targetId}`).addClass("active");
      }
    });
  };

  avatarText = (name) => {
    let initial = "";
    const names = name.split(" ");
    names.forEach((name) => {
      initial += name.charAt(0);
    });
    return initial;
  };

  loguserout = (e) => {
    e.preventDefault();
    this.props.logout();
  };

  render() {
    // const Child = this.state.child;
    // const { user } = this.props.auth;

    return (
      <div id="wrapper">
        <div id="content-wrapper" className="d-flex flex-column">
          <footer className="sticky-footer bg-white">
            <div className="container my-auto">
              <div className="copyright text-center my-auto">
                <span>Copyright &copy; Eshop {new Date().getFullYear()}</span>
              </div>
            </div>
          </footer>
        </div>
      </div>
    );
  }
}

Dashboard.propTypes = {
  auth: PropTypes.object.isRequired,
  logout: PropTypes.func.isRequired,
  nestRoute: PropTypes.elementType.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logout })(Dashboard);
