import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getproduct } from "../../../actions/productAction";
import Product from "../../general/Product";

const ProductsSeller = () => {
  const dispatch = useDispatch();
  const { product, auth } = useSelector((state) => ({
    product: state.product,
    auth: state.auth,
  }));

  useEffect(() => {
    dispatch(getproduct());
  }, [dispatch]);

  const merchantProduct = product?.product?.filter(
    (prod) => prod.userId === auth.user._id
  ) || [];

  const productDetails = (product) => (
    <ul>
      <li>Rupees: {product.price}</li>
      <li>Quantity: {product.quantity}</li>
    </ul>
  );

  return (
    <div>
      {merchantProduct.map((product, index) => (
        <Product
          key={index}
          product={product}
          description={() => productDetails(product)}
          buttonName="add images"
        />
      ))}
      <p>hello product</p>
    </div>
  );
};

export default ProductsSeller;
