import React, { useEffect } from "react";
import { Switch, Route, useLocation } from "react-router-dom";
import { connect } from "react-redux";
import PropTypes from "prop-types";
import Sidebar from "./Sidebar";
import Topbar from "./Topbar";
import { logout } from "../../actions/authAction";
import Home from "../Home"; // Assuming these components exist
import AddProduct from "../AddProduct";
import Product from "../Product";

const Dashboard = ({ auth, logout }) => {
  const location = useLocation();

  useEffect(() => {
    activeNav();
  }, [location.pathname]);

  const activeNav = () => {
    const pathname = location.pathname;
    const possibleRoutes = [
      { route: "/dashboard", targetId: "home" },
      { route: "/dashboard/addproduct", targetId: "AddProduct" },
      { route: "/dashboard/product", targetId: "product" },
    ];
    possibleRoutes.forEach(({ route, targetId }) => {
      const element = document.getElementById(targetId);
      if (element) {
        element.classList.remove("active");
      }
      if (route === pathname) {
        if (element) {
          element.classList.add("active");
        }
      }
    });
  };

  const loguserout = (e) => {
    e.preventDefault();
    logout();
  };

  const { user } = auth;

  return (
    <div id="wrapper">
      <Sidebar />
      <div id="content-wrapper" className="d-flex flex-column">
        <div id="content">
          <Topbar user={user} loguserout={loguserout} />
          <Switch>
            <Route path="/dashboard/addproduct" component={AddProduct} />
            <Route path="/dashboard/product" component={Product} />
            <Route path="/dashboard" component={Home} />
          </Switch>
        </div>
        <footer className="sticky-footer bg-white">
          <div className="container my-auto">
            <div className="copyright text-center my-auto">
              <span>Copyright &copy; Eshop {new Date().getFullYear()}</span>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

Dashboard.propTypes = {
  auth: PropTypes.object.isRequired,
  logout: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { logout })(Dashboard);
