import { GET_PRODUCTS, PRODUCT_ERROR } from "../actions/types";

const initialState = {
  products: [],
  product: {},
  errors: {},
};

const productReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_PRODUCTS:
      return {
        ...state,
        products: payload,
      };
    case PRODUCT_ERROR:
      return {
        ...state,
        errors: {
          ...state.errors,
          ...payload,
        },
      };
    default:
      return state;
  }
};

export default productReducer;
