import React, { Component } from "react";
import Input from "../../general/Input";
export default class Addproduct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      description: "",
      price: "",
      quantitiy: "",
      category: "",
    };
  }
  onChange = (e) => {
    this.setState({[e.target.name]: e.target.value})
  };
  onSubmit=()=>{
    const {name,description,price,brand,quantitiy,category}=this.state;
    const newProduct={name,
      description,
      price,
      brand,
      quantitiy,category}      
  }
  render() {
    const { name, description, price, brand, quantitiy, category } = this.state;
    return (
      <div style={{ textAlign: "center" }}>
        <h1>Addproduct</h1>
        <Input
          type="text"
          placeholder="name of product"
          name="name"
          value={name}
          onChange={this.onChange} 
        />
        <Input
          type="text"
          placeholder="give a brief description of the product"
          name="name"
          value={description}
          onChange={this.onChange}
        />
        <Input
          type="number"
          placeholder="Enter the price of the product"
          name="price"
          value={price}
          onChange={this.onChange}
        />
        <Input
          type="text"
          placeholder="Enter the brand of the product"
          name="name"
          value={brand}
          onChange={this.onChange}
        />
        <Input
          type="number"
          placeholder="number of the product"
          name="quantitiy"
          value={quantitiy}
          onChange={this.onChange}
        />
        <div>
          <select name="category" value={category} onChange={this.onChange}>
            <option value="0">Select a category for the product </option>
            <option value="Clothing">Clothing</option>
            <option value="Clothing">Clothing</option>
            <option value="Clothing">Clothing</option>
            <option value="Clothing">Clothing</option>
            <option value="Clothing">Clothing</option>
          </select>
        </div>
        <button>Submit</button>
      </div>
    );
  }
}
