import React, { Component } from "react";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";

class Products extends Component {
  state = {
    merchantProduct: [],
  };

  componentDidMount() {
    this.props.getproduct();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.products !== this.props.products && this.props.products.products.length > 0) {
      const userId = this.props.auth.user._id;
      const merchantProduct = this.props.products.products.filter(
        (product) => product.userId === userId
      );
      this.setState({ merchantProduct });
      console.log(merchantProduct);
    }
  }

  render() {
    const { merchantProduct } = this.state;
    console.log("==>>",pr);
    return (
      <div>
        {/* <h2>Products</h2> */}
        <ul>
          {merchantProduct.map((product) => (
            <li key={product._id}>{product.name}</li>
          ))}
        </ul>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.Products,
  auth: state.auth,
});

export default connect(mapStateToProps, { getproduct })(Products);
