import React, { Component } from "react";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";
import Product from "../../general/Product";
import "../../general"

class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merchantProduct: [],
    };
  }

  componentDidMount() {
    this.props.getproduct();
  }

  componentDidUpdate(prevProps) {
    const { product, auth } = this.props;
    if (product && product.product && prevProps.product !== product) {
      const userId = auth.user._id;
      const merchantProduct = product.product.filter(
        (prod) => prod.userId === userId
      );
      this.setState({ merchantProduct });
    }
  }

  productDetails = (product) => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };

  render() {
    const { merchantProduct } = this.state;
    return (
      <div>
        {merchantProduct.map((product, index) => (
          <Product
            key={index}
            product={product}
            description={() => this.productDetails(product)}
            buttonName="add images"
          />
        ))}
        <p>hello product</p>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  product: state.product,
  auth: state.auth,
});

export default connect(mapStateToProps, { getproduct })(Products);
