import React, { Component } from "react";
import { connect } from "react-redux";
import { getproduct } from "../../../actions/productAction";
import Product from "../../general/Product";

class Products extends Component {
  constructor(props) {
    super(props);
    this.state = {
      merchantProducts: [],
    };
  }

  componentDidMount() {
    this.props.getproduct();
  }

  componentDidUpdate(prevProps) {
    if (
      prevProps.products !== this.props.products &&
      this.props.products.products.length > 0
    ) {
      const userId = this.props.auth.user._id;
      const merchantProducts = this.props.products.products.filter(
        (product) => product.userId === userId
      );
      this.setState({ merchantProducts });
    }
  }

  productDetails = (product) => (
    <ul>
      <li>Rupees: {product.price}</li>
      <li>Quantity: {product.quantity}</li>
    </ul>
  );

  render() {
    const { merchantProducts } = this.state;
    return (
      <div className="row">
        {merchantProducts.map((product, index) => (
          <Product
            key={product._id} // Add key prop for React list rendering
            product={product}
            description={this.productDetails(product)}
            buttonName="addimg"
          />
        ))}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  products: state.products,
  auth: state.auth,
});

export default connect(mapStateToProps, { getproduct })(Products);
