import React from "react";
import { Card, Button } from "antd";
import PropTypes from "prop-types"; // Corrected import

const { Meta } = Card;

const Product = ({ product, description, buttonName }) => {
  return (
    <div>
      <Card
        style={{ width: 300 }}
        cover={
          <img
            alt="example"
            src="https://gw.alipayobjects.com/zos/rmsportal/JiqGstEfoWAOHiTxclqi.png"
          />
        }
      >
        <Meta title={product.name} description={description} />
        <Button type="primary">{buttonName}</Button>
      </Card>
    </div>
  );
};

Product.propTypes = {
  product: PropTypes.shape({
    name: PropTypes.string.isRequired,
    // Add other required properties of product here
  }).isRequired,
  description: PropTypes.string.isRequired, // Corrected PropTypes
  buttonName: PropTypes.string, // Optional PropTypes
};

export default Product;
