import React, { Component } from 'react';
import { connect } from 'react-redux';
import {getproduct} from "../../../actions/productAction"
import Product from '../../general/Product'; 
class Products extends Component {
constructor(props){
  super(props)
  this.state={
    merchantProduct:[]
  };
}
  componentDidMount(){
    this.props.getproduct();
  }
  componentWillReceiveProps(nextProps){
    if (nextProps && 
      nextProps.products && 
      nextProps.products.products.length > 0) {
      const userId = nextProps.auth.user._id;
      let merchantProduct = []
      merchantProduct = nextProps.products.products.filter(
        (product)=>product.userId===userId
      );
      this.setState({merchantProduct});
    }
  }

  productDetails = (product) => {
    return (
      <ul>
        <li>Rupees: {product.price}</li>
        <li>Quantity: {product.quantity}</li>
      </ul>
    );
  };

  render() {
    const {merchantProducts} =this.state
    return (
      <div className='row'>
        {merchantProducts.map((product, index)=>
        <Product product={product} description={this} buttonName="Add images"/>
        )}
      </div>
    )
  }
}
const mapStateToProps = (state)=>({
  products: state.products,
  auth: state.auth,
});
export default connect (mapStateToProps,{ getproduct})(Products)
